                  <bds-typo variant="fs-20" bold="bold" margin={false}>
                    {item.title}
                  </bds-typo>
                  <bds-typo variant="fs-16">{item.subTitle}</bds-typo>
                  <bds-button>Saiba mais</bds-button>
                </bds-grid>
              </bds-grid>
            </bds-carousel-item>
